#include<stdlib.h>
#include<string.h>
#include<stdio.h>

#define POINT_LIMIT 100

struct vector //动态数组
{
    int *Array;
    int Len;
    int BatchSize;
    int *ArrayNew;
    void init()
    {
        Array = NULL;//存放数组
        ArrayNew = NULL;//临时存储
        Len = 0;//数组长度
        BatchSize = 0;//内存块大小
    }
    vector()//构造函数
    {
        Array = NULL;
        ArrayNew = NULL;
        Len = 0;
        BatchSize = 0;
    }
    void clear()//清空该数组
    {
        if(Len == 0)return ;
        Len = 0;
        free(Array);
    }
    void push_back(int Num)//向该数组的末尾添加一个元素
    {
        if(Len+1<=BatchSize)
        {
            Array[Len++] = Num;
        }
        else
        {
            //如果数组长度超过内存块的大小则将块大小乘以2
            if(Len)
            {
                BatchSize<<=1;
                ArrayNew = (int *)malloc(sizeof(int)*BatchSize);
                memcpy(ArrayNew,Array,sizeof(int)*Len);
                free(Array);
            }
            else
            {
                ArrayNew = (int *)malloc(sizeof(int));
                BatchSize=1;
            }
            Array = ArrayNew;
            Array[Len++] = Num;
        }

    }
    void pop_back()//将该数组末尾元素删除
    {
        if(Len < 1)return;
        if(Len == 1)
        {
            Len--;
            free(Array);
            BatchSize = 0;
            return;
        }
        //删除一个元素后如果数组长度小于内存块大小的一半则将内存块大小/2
        if(Len-1 <= (BatchSize>>1))
        {
            BatchSize>>=1;
            ArrayNew = (int *)malloc(sizeof(int)*BatchSize);
            memcpy(ArrayNew,Array,sizeof(int)*BatchSize);
            free(Array);
            Array = ArrayNew;
            Len--;
        }
        else Len--;

    }
    int size() const
    {
        return Len;
    }
    int &operator [] (int i) const
    {
        return Array[i];
    }
    int batch_size() const
    {
        return BatchSize;
    }
    vector &operator = (const vector &vec)
    {
        if(Array!=NULL)free(Array);
        Array = (int *)malloc(sizeof(int)*vec.batch_size());
        memcpy(Array,vec.Array,sizeof(int)*vec.batch_size());
        Len = vec.size();
        BatchSize = vec.batch_size();
        return (*this);
    }
};
//用于储存数字
typedef struct Data
{
    vector number;//值
    int point; //小数点位数
    int symbol;//符号
    int error; //错误属性
    /*
    1. 非法字符
    2. 语法错误
    3. 数学错误
    4. 数字过大
    */
    Data &operator = (const Data &b)
    {
        number = b.number;
        point = b.point;
        symbol = b.symbol;
        error =b.error;
        return (*this);
    }
}Data;

//定义栈里的元素
typedef struct StackMember
{
    struct StackMember *next;
    Data data;

}StackMember;

//定义栈
typedef struct Stack
{
    struct StackMember *top;
    int size;
}Stack;

vector getE()
{
    vector vE;
    char sE[1005]="";
    for(int i=0;i<=POINT_LIMIT;i++)
        vE.push_back(sE[POINT_LIMIT-i]-'0');
    return vE;
}

vector getPi()
{
    vector vPi;
    char sPi[1005]="";
    for(int i=0;i<=POINT_LIMIT;i++)
        vPi.push_back(sPi[POINT_LIMIT-i]-'0');
    return vPi;
}

Data InttoData(int x)
{
    Data rlt;
    rlt.number.init();
    rlt.symbol=x<0?-1:1;
    rlt.error=0;
    x=x<0?-x:x;
    if(!x) rlt.number.push_back(x);
    while(x){
        rlt.number.push_back(x%10);
        x/=10;
    }
    rlt.point=0;
    return rlt;
}

Data Error(int x)
{
    Data error;
    error=InttoData(0);
    error.error=x;
    return error;
}

//初始化栈
Stack *Init()
{
    Stack *s = (Stack *)malloc(sizeof(Stack));//申请栈的内存空间,并将空间的地址传给栈数据类型的指针.
    s->top=NULL;
    s->size=0;
    return s;//返回初始话栈的地址
}

//初始化栈的成员
StackMember *InitStackMember(Data x)
{
    StackMember *tempStackMember=(StackMember *)malloc(sizeof(StackMember));
    tempStackMember->data.number.init();
    tempStackMember->data=x;
    tempStackMember->next=NULL;
    return tempStackMember;
}

//判断栈是否为空
bool Empty(Stack *s)
{
    return s->top==NULL;//如果栈为空，栈顶为-1
}

//返回栈顶元素
StackMember *Top(Stack *s)
{
    if (Empty(s))//判断栈是否为空
        return NULL;
    return s->top;//返回栈顶元素
}

//入栈操作
bool Push(Stack *s,StackMember *val)
{
    if (s==NULL)//判断栈是否存在
        return false;
    val->next=s->top;
    s->top=val;
    (s->size)++;
    return true;
}

//出栈操作
bool Pop(Stack *s)
{
    if (s == NULL)//判断栈是否存在
        return false;
    if (Empty(s))//判断栈内是否为空
        return false;
    StackMember *topMember=s->top;
    s->top=s->top->next;
    free(topMember); //释放出栈成员的空间
    (s->size)--;
    return true;
}

